mapper.select:
SELECT a.team_total, a.un_qualified_increment, a.qualified_increment, a.excellent_increment, a.sign_increment, a.family_increment, a.result_score, f.address org_address, f.org_name, (SELECT COUNT(DISTINCT a.person_id) FROM t_person_desease_info a, t_person_information b, t_user_doc_sign c, t_doctor d, t_appraisal_month_org_score e WHERE a.person_id = b.person_id AND b.family_id = c.family_id AND c.doc_team_id = d.team_id and e.org_id =d.org_id
and d.org_id=#{orgId}
and date_format(a.create_time, '%Y%m') = #{month}
AND( a.adult_flag = '1' OR a.child_flag = '1' OR a.diabetes_flag = '1' OR a.hyper_flag = '1' OR a.major_psychosis_flag = '1' OR a.maternal_flag = '1' OR a.oldm_flag ='1')) chronic_disease_number FROM t_appraisal_month_org_score a, t_hospital f WHERE a.org_id = f.id
and a.org_id=#{orgId}
and a.month=#{month}

SELECT count(a.org_id ) FROM t_appraisal_month_org_score a, t_hospital b WHERE a.org_id = b.id
and a.month=#{month}
and a.org_id=#{orgId}

SELECT a.org_id, a.sign_increment, a.family_increment, a.sign_hyper_score, a.sign_diabetes_score, a.sign_phychosis_score, a.sign_old_score, a.sign_maternal_score, a.sign_children_score, a.sign_manage_score, a.public_health_hyper_score, a.public_health_diabetes_score, a.public_health_phychosis_score, a.public_health_old_score, a.public_health_maternal_score, a.public_health_children_score, a.public_health_score, a.health_manage_edu_score, a.health_manage_daily_activity_score, a.health_manage_assessment_score, a.health_manage_document_score, a.health_manage_score, a.customer_score, a.result_score, a.team_total, a.un_qualified_increment, a.qualified_increment, a.excellent_increment, a.month, a.create_time,b.org_name FROM t_appraisal_month_org_score a, t_hospital b WHERE a.org_id = b.id
and a.month=#{month}
and a.org_id=#{orgId}
