mapper.select:
SELECT a.team_id, a.month, b.doc_name doctor_name, a.sign_increment, a.family_increment, a.sign_manage_score, a.public_health_score, a.health_manage_score, a.customer_score, a.result_score FROM t_appraisal_month_team_score a, t_doctor b WHERE a.team_id = b.team_id AND b.leader_flag = "1"
and a.team_id in ( SELECT team_id from t_doctor c where c.doc_name LIKE CONCAT('%', #{doctorName}, "%") )
and a.month =#{month}
and b.org_id =#{orgId}

SELECT count(b.doc_name)
FROM t_appraisal_month_team_score a, t_doctor b WHERE a.team_id = b.team_id AND b.leader_flag = "1"
and a.team_id in ( SELECT team_id from t_doctor c where c.doc_name LIKE CONCAT('%', #{doctorName}, "%") )
and a.month =#{month}
and b.org_id =#{orgId}

SELECT a.result_score, a.sign_increment, a.family_increment, f.address org_address, f.org_name, b.doc_name doctor_name, (SELECT COUNT(a.person_id) FROM t_person_desease_info a, t_person_information b, t_user_doc_sign c, t_appraisal_month_team_score d WHERE a.person_id = b.person_id AND b.family_id = c.family_id and d.team_id =c.doc_team_id
AND c.doc_team_id =#{teamId}
and date_format(a.create_time, '%Y%m') = #{month}
AND( a.adult_flag = '1' OR a.child_flag = '1' OR a.diabetes_flag = '1' OR a.hyper_flag = '1' OR a.major_psychosis_flag = '1' OR a.maternal_flag = '1' OR a.oldm_flag ='1')) chronic_disease_number FROM t_appraisal_month_team_score a, t_doctor b, t_hospital f WHERE a.team_id = b.team_id AND b.org_id = f.id and b.leader_flag="1"
and a.team_id =#{teamId}
and a.month =#{month}
